def lijk(event):
    event.reply(random.choice([x for x in txt.split(", ")]))

def lijken(event):
    for lijk in txt.split(", "):
        event.reply(lijk)

def chop(event):
    event.reply("\n".join([lijk.split("lijk")[0] for lijk in txt.split(", ")]))
