"""Generic Functions"""
def less_than(mine, yours):
    return mine < yours

def less_than_equal(mine, yours):
    return mine <= yours

def greater_than(mine, yours):
    return mine > yours

def greater_than_equal(mine, yours):
    return mine >= yours

def equal_to(mine, yours):
    return mine == yours

def not_equal_to(mine, yours):
    return mine != yours
